widgets.css:
.pad-0 {
  --color-fill: red;
  --color-stroke: red;
}
.pad-1 {
  --color-fill: orange;
  --color-stroke: orange;
}
.pad-2 {
  --color-fill: yellow;
  --color-stroke: yellow;
}
.pad-3 {
  --color-fill: GreenYellow;
  --color-stroke: GreenYellow;
}
.pad-4 {
  --color-fill: forestgreen;
  --color-stroke: forestgreen;
}
.pad-5 {
  --color-fill: MediumTurquoise;
  --color-stroke: MediumTurquoise;
}
.pad-6 {
  --color-fill: RoyalBlue;
  --color-stroke: RoyalBlue;
}
.pad-7 {
  --color-fill: DarkViolet;
  --color-stroke: DarkViolet;
}

